import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Card, Button } from 'react-bootstrap'; // Import Bootstrap Card components
import Home from './pages/Home';

function App() {
  const [studentGroups, setStudentGroups] = useState(null);

  useEffect(() => {
    const fetchGroups = async () => {
      try {
        const response = await fetch("/api/studentgroups");
        const json = await response.json();

        if (response.ok) {
          setStudentGroups(json);
        }
      } catch (error) {
        console.error("Error fetching student groups:", error);
      }
    };

    fetchGroups();
  }, []);

  return (
    <div className="App">
      <BrowserRouter>
        <Home />
        <div className='studentgroups'>
          {studentGroups &&
            studentGroups.map((group) => (
              <Card key={group._id} style={{ width: '18rem' }}>
                <Card.Body>
                  <Card.Title>{group.name}</Card.Title>
                  <Card.Text>{group.description}</Card.Text>
                  <Link to={`/groups/${group._id}`}>
                    <Button variant="primary">View Details</Button>
                  </Link>
                </Card.Body>
              </Card>
            ))}
        </div>
      </BrowserRouter>
    </div>
  );
}

export default App;
